import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';

export default function App() {
  const [game, setGame] = useState({
    a1: '', a2: '', a3: '',
    b1: '', b2: '', b3: '',
    c1: '', c2: '', c3: '',
    playerOne: {
      name: "player 1",
      myTime: true,
      ballOrX: "x"
    },
    playertwo: {
      name: "player 2",
      myTime: false,
      ballOrX: "o"
    },
    plays: []
  });

  function next() {
    if (game.playerOne.myTime === true) {
      return game.playerOne.name;
    } else {
      game.playertwo.name;
    }
  }

  function msg() {
    if (game.playerOne.count == 0) {
      return " this is your time!";
    }
  }

  function cellIsFilled(cell) {

    if (cell == 'a1') {
      return game.a1 != '';
    } else if (cell == 'a2') {
      return game.a2 != '';
    } else if (cell == 'a3') {
      return game.a3 != '';
    } else if (cell == 'b1') {
      return game.b1 != '';
    } else if (cell == 'b2') {
      return game.b2 != '';
    } else if (cell == 'b3') {
      return game.b3 != '';
    } else if (cell == 'c1') {
      return game.c1 != '';
    } else if (cell == 'c2') {
      return game.c2 != '';
    } else if (cell == 'c3') {
      return game.c3 != '';
    }
  }

  function currentPlayerBallOrX() {
    return game.playerOne.myTime ? game.playerOne.ballOrX : game.playertwo.ballOrX;
  }

  function currentPlayer() {
    return game.playerOne.myTime ? 1 : 2;
  }

  function SetCell(updateGame, cell, ballOrX) {
    if (cell == 'a1') {
      updateGame.a1 = ballOrX;
    } else if (cell == 'a2') {
      updateGame.a2 = ballOrX;
    } else if (cell == 'a3') {
      updateGame.a3 = ballOrX;
    } else if (cell == 'b1') {
      updateGame.b1 = ballOrX;
    } else if (cell == 'b2') {
      updateGame.b2 = ballOrX;
    } else if (cell == 'b3') {
      updateGame.b3 = ballOrX;
    } else if (cell == 'c1') {
      updateGame.c1 = ballOrX;
    } else if (cell == 'c2') {
      updateGame.c2 = ballOrX;
    } else if (cell == 'c3') {
      updateGame.c3 = ballOrX;
    }
    return updateGame;
  }

  function RecordPlay(updateGame, current, ballOrX, cell) {
    const objPlay = { player: current, ballOrX: ballOrX, cell, dateTime: new Date() };
    updateGame.plays.push(objPlay);
    return updateGame;
  }

  function GetACopyOfTheObject() {
    var auxnowGame = game;
    auxnowGame = JSON.stringify(auxnowGame);
    return JSON.parse(auxnowGame);
  }

  function GetGameIsOver(updateGame) {
    if (updateGame.plays.length >= 5) {
      if (updateGame.a1 == updateGame.a2 && updateGame.a2 == updateGame.a3) {
        return true;
      }else if (updateGame.a1 == updateGame.b1 && updateGame.b1 == updateGame.c1) {
        return true;
      }
    }else{
      return false;
    }
  }

  function selectField(cell) {
    var updateGame = GetACopyOfTheObject();

    const isFilled = cellIsFilled(cell);

    if (isFilled == false) {
      const ballOrX = currentPlayerBallOrX();
      updateGame = SetCell(updateGame, cell, ballOrX);
      const current = currentPlayer();
      updateGame = RecordPlay(updateGame, current, ballOrX, cell);
      GetGameIsOver(updateGame);
      setGame(updateGame);
    }
  }

  return (
    <View style={styles.container}>
      <View style={styles.containerCenter}>
        <Text style={[styles.boxText]} >jogadas: {game.plays.length} </Text>
        <Text style={[styles.boxText]} >{next()} {msg()}</Text>
      </View>

      <View style={styles.containerCenter}>
        <TouchableOpacity onPress={() => selectField('a1')}>
          <View style={[styles.box, styles.borderColor, styles.borderBottom, styles.borderRight]}>
            <Text style={[styles.boxText]} >{game.a1}</Text>
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => selectField('a2')} >
          <View style={[styles.box, styles.borderColor, styles.borderBottom, styles.borderLeft, styles.borderRight]}>
            <Text style={[styles.boxText]} >{game.a2}</Text>
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => selectField('a3')} >
          <View style={[styles.box, styles.borderColor, styles.borderBottom, styles.borderLeft]}>
            <Text style={[styles.boxText]} >{game.a3}</Text>
          </View>
        </TouchableOpacity>
      </View>
      <View style={styles.containerCenter}>
        <TouchableOpacity onPress={() => selectField('b1')} >
          <View style={[styles.box, styles.borderColor, styles.borderTop, styles.borderBottom, styles.borderRight]}>
            <Text style={[styles.boxText]} >{game.b1}</Text>
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => selectField('b2')} >
          <View style={[styles.box, styles.borderColor, styles.borderTop, styles.borderBottom, styles.borderLeft, styles.borderRight]}>
            <Text style={[styles.boxText]} >{game.b2}</Text>
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => selectField('b3')} >
          <View style={[styles.box, styles.borderColor, styles.borderTop, styles.borderLeft, styles.borderBottom]}>
            <Text style={[styles.boxText]} >{game.b3}</Text>
          </View>
        </TouchableOpacity>
      </View>
      <View style={styles.containerCenter}>
        <TouchableOpacity onPress={() => selectField('c1')} >
          <View style={[styles.box, styles.borderColor, styles.borderTop, styles.borderRight]}>
            <Text style={[styles.boxText]} >{game.c1}</Text>
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => selectField('c2')} >
          <View style={[styles.box, styles.borderColor, styles.borderTop, styles.borderRight, styles.borderLeft]}>
            <Text style={[styles.boxText]} >{game.c2}</Text>
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => selectField('c3')} >
          <View style={[styles.box, styles.borderColor, styles.borderTop, styles.borderLeft]}>
            <Text style={[styles.boxText]} >{game.c3}</Text>
          </View>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  containerCenter: {
    flexDirection: 'row',

  },
  box: {
    height: 70,
    width: 70,
  },
  bgPrimary: {
    backgroundColor: '#007bff'
  },
  borderTop: {
    borderTopWidth: 1,
  },
  borderBottom: {
    borderBottomWidth: 1,
  },
  borderLeft: {
    borderLeftWidth: 1,
  },
  borderRight: {
    borderRightWidth: 1,
  },
  borderColor: {
    borderColor: '#007bff',
  },
  boxText: {
    fontSize: '25px',
    textAlign: 'center',
    alignSelf: 'center'
  }
});
