import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, TouchableOpacity, Animated, ImageBackground, SafeAreaView, TextInput } from 'react-native';
import { Entypo, Fontisto, MaterialCommunityIcons, FontAwesome5, Feather } from '@expo/vector-icons';

export default function App() {
  // const image = { uri: "https://e7.pngegg.com/pngimages/376/43/png-clipart-paper-leaf-leaf-white-leaf.png" };
  const image = { uri: "" };
  const [darkMode, setDarkMode] = useState(true);

  const [editPlayerOne, setEditPlayerOne] = useState(false);
  const [editPlayerTwo, setEditPlayerTwo] = useState(false);

  const [playingWithRobot, setPlayingWithRobot] = useState(false);

  const [onChangeTextp1, setOnChangeTextp1] = useState("");
  const [onChangeTextp2, setOnChangeTextp2] = useState("");


  const [game, setGame] = useState({
    a1: '', a2: '', a3: '',
    b1: '', b2: '', b3: '',
    c1: '', c2: '', c3: '',

    contrastA1: false, contrastA2: false, contrastA3: false,
    contrastB1: false, contrastB2: false, contrastB3: false,
    contrastC1: false, contrastC2: false, contrastC3: false,

    playerOne: {
      name: "player 1",
      myTime: true,
      ballOrX: "x"
    },
    playerTwo: {
      name: "player 2",
      myTime: false,
      ballOrX: "o"
    },
    robot: {
      name: "Robot"
    },
    plays: []
  });

  function next() {
    if (game.playerOne.myTime === true) {
      return game.playerOne.name;
    } else {
      return game.playerTwo.name;
    }
  }

  function msg() {
    if (game.playerOne.count == 0) {
      return " this is your time!";
    }
  }

  function cellIsFilled(cell) {

    if (cell == 'a1') {
      return game.a1 != '';
    } else if (cell == 'a2') {
      return game.a2 != '';
    } else if (cell == 'a3') {
      return game.a3 != '';
    } else if (cell == 'b1') {
      return game.b1 != '';
    } else if (cell == 'b2') {
      return game.b2 != '';
    } else if (cell == 'b3') {
      return game.b3 != '';
    } else if (cell == 'c1') {
      return game.c1 != '';
    } else if (cell == 'c2') {
      return game.c2 != '';
    } else if (cell == 'c3') {
      return game.c3 != '';
    }
  }

  function currentPlayerBallOrX() {
    return game.playerOne.myTime ? game.playerOne.ballOrX : game.playerTwo.ballOrX;
  }

  function currentPlayer() {
    return game.playerOne.myTime ? 1 : 2;
  }

  function SetCell(updateGame, cell, ballOrX) {
    if (cell == 'a1') {
      updateGame.a1 = ballOrX;
    } else if (cell == 'a2') {
      updateGame.a2 = ballOrX;
    } else if (cell == 'a3') {
      updateGame.a3 = ballOrX;
    } else if (cell == 'b1') {
      updateGame.b1 = ballOrX;
    } else if (cell == 'b2') {
      updateGame.b2 = ballOrX;
    } else if (cell == 'b3') {
      updateGame.b3 = ballOrX;
    } else if (cell == 'c1') {
      updateGame.c1 = ballOrX;
    } else if (cell == 'c2') {
      updateGame.c2 = ballOrX;
    } else if (cell == 'c3') {
      updateGame.c3 = ballOrX;
    }
    return updateGame;
  }

  function RecordPlay(updateGame, current, ballOrX, cell) {
    const objPlay = { player: current, ballOrX: ballOrX, cell, dateTime: new Date() };
    updateGame.plays.push(objPlay);
    return updateGame;
  }

  function GetACopyOfTheObject() {
    var auxnowGame = game;
    auxnowGame = JSON.stringify(auxnowGame);
    return JSON.parse(auxnowGame);
  }

  function GetGameIsOver(updateGame) {
    if (updateGame.plays.length >= 5) {
      if (updateGame.a1 != '' && updateGame.a1 == updateGame.a2 && updateGame.a2 == updateGame.a3) {
        updateGame.contrastA1 = true;
        updateGame.contrastA2 = true;
        updateGame.contrastA3 = true;
        return true;
      }
      else if (updateGame.b1 != '' && updateGame.b1 == updateGame.b2 && updateGame.b2 == updateGame.b3) {
        updateGame.contrastB1 = true;
        updateGame.contrastB2 = true;
        updateGame.contrastB3 = true;
        return true;
      } else if (updateGame.a1 != '' && updateGame.a1 == updateGame.b1 && updateGame.b1 == updateGame.c1) {
        updateGame.contrastA1 = true;
        updateGame.contrastB1 = true;
        updateGame.contrastC1 = true;
        return true;
      } else if (updateGame.a1 != '' && updateGame.a1 == updateGame.b2 && updateGame.b2 == updateGame.c3) {
        updateGame.contrastA1 = true;
        updateGame.contrastB2 = true;
        updateGame.contrastC3 = true;
        return true;
      } else if (updateGame.a2 != '' && updateGame.a2 == updateGame.b2 && updateGame.b2 == updateGame.c2) {
        updateGame.contrastA2 = true;
        updateGame.contrastB2 = true;
        updateGame.contrastC2 = true;
        return true;
      } else if (updateGame.a3 != '' && updateGame.a3 == updateGame.b3 && updateGame.b3 == updateGame.c3) {
        updateGame.contrastA3 = true;
        updateGame.contrastB3 = true;
        updateGame.contrastC3 = true;
        return true;
      } else if (updateGame.c1 != '' && updateGame.c1 == updateGame.c2 && updateGame.c2 == updateGame.c3) {
        updateGame.contrastC1 = true;
        updateGame.contrastC2 = true;
        updateGame.contrastC3 = true;
        return true;
      } else if (updateGame.c1 != '' && updateGame.c1 == updateGame.b2 && updateGame.b2 == updateGame.a3) {
        updateGame.contrastC1 = true;
        updateGame.contrastB2 = true;
        updateGame.contrastA3 = true;
        return true;
      }
    }
    return false;
  }

  function ResetGame() {

    const reserGame = GetACopyOfTheObject();
    reserGame.a1 = '';
    reserGame.a2 = '';
    reserGame.a3 = '';

    reserGame.b1 = '';
    reserGame.b2 = '';
    reserGame.b3 = '';

    reserGame.c1 = '';
    reserGame.c2 = '';
    reserGame.c3 = '';

    reserGame.contrastA1 = false;
    reserGame.contrastA2 = false;
    reserGame.contrastA3 = false;

    reserGame.contrastB1 = false;
    reserGame.contrastB2 = false;
    reserGame.contrastB3 = false;

    reserGame.contrastC1 = false;
    reserGame.contrastC2 = false;
    reserGame.contrastC3 = false;

    reserGame.plays = [];

    const bool = Math.random() < 0.5;
    reserGame.playerOne.myTime = bool;
    reserGame.playerTwo.myTime = !bool;

    setGame(reserGame);
  }

  function ChangePlayer(updateGame) {
    updateGame.playerOne.myTime = !updateGame.playerOne.myTime;
    updateGame.playerTwo.myTime = !updateGame.playerTwo.myTime;
    return updateGame;
  }

  function selectField(cell) {
    var updateGame = GetACopyOfTheObject();

    const isFilled = cellIsFilled(cell);
    const isOver = GetGameIsOver(updateGame);
    if (isOver == false) {
      if (isFilled == false) {

        const ballOrX = currentPlayerBallOrX();
        updateGame = SetCell(updateGame, cell, ballOrX);
        const current = currentPlayer();
        updateGame = RecordPlay(updateGame, current, ballOrX, cell);
        updateGame = ChangePlayer(updateGame);
        GetGameIsOver(updateGame);
        setGame(updateGame);
      }
    } else {
      reserGame();
    }
  }

  function whoWon() {
    const last = game.plays[game.plays.length - 1];
    if (last.player == 1) {
      return game.playerOne.name;
    } else {
      return game.playerTwo.name;
    }

  }

  const ValidateName = (name) => {
    return String(name).trim();
  }

  const playRobot = () => {

  }

  const ChangeName = (player) => {
    var updateGame = GetACopyOfTheObject();
    if (player == 'p1') {
      const nameOk = ValidateName(onChangeTextp1);
      if (nameOk.length > 2) {
        updateGame.playerOne.name = nameOk;
      } else {
        updateGame.playerOne.name = 'Player 1';
        setOnChangeTextp1('');
      }
      setEditPlayerOne(false);
    } else {
      const nameOk = ValidateName(onChangeTextp2);
      if (nameOk.length > 2) {
        updateGame.playerTwo.name = nameOk;
      } else {
        updateGame.playerTwo.name = 'Player 2';
        setOnChangeTextp2('');
      }
      setEditPlayerTwo(false);
    }
    setGame(updateGame);
  }

  return (
    <SafeAreaView style={{ flex: 1, alignItems: "center", justifyContent: "center", width: '100%' }}>
      <View style={[styles.container, { backgroundColor: darkMode ? "#232323" : "#e0e0e0" }]}>
        <ImageBackground source={image} resizeMode="contain" style={[styles.image, { padding: 10 }]}>
          {/* <View style={styles.container}> */}
          {GetGameIsOver(game) === false && game.plays.length <= 8 &&
            <View style={styles.playerBoxes}>

              <View style={[styles.playerBox]} >
                {editPlayerOne === false &&
                  <Text onPress={() => setEditPlayerOne(!editPlayerOne)} style={[game.playerOne.myTime ? styles.selectedPlayerBox : styles.notSelectedPlayerBox, styles.boxTextPlayer]} >{game.playerOne.name}</Text>
                }
                {editPlayerOne &&
                  <TextInput
                    style={styles.input}
                    onChangeText={setOnChangeTextp1}
                    onBlur={() => ChangeName('p1')}
                    value={onChangeTextp1}
                    style={styles.input}
                  />
                }
              </View>

              {playingWithRobot === false &&
                <View style={[styles.playerBox]} >
                  {editPlayerTwo === false &&
                    <Text onPress={() => setEditPlayerTwo(!editPlayerTwo)} style={[game.playerTwo.myTime ? styles.selectedPlayerBox : styles.notSelectedPlayerBox, styles.boxTextPlayer, { textAlign: 'right' }]} >{game.playerTwo.name}</Text>
                  }
                  {editPlayerTwo &&
                    <TextInput
                      style={styles.input}
                      onChangeText={setOnChangeTextp2}
                      onBlur={() => ChangeName('p2')}
                      value={onChangeTextp2}
                      style={styles.input}
                    />
                  }
                </View>
              }
              {playingWithRobot === true &&
                <View style={[styles.playerBox]} >
                  {editPlayerTwo === false &&
                    <Text style={[game.playerTwo.myTime ? styles.selectedPlayerBox : styles.notSelectedPlayerBox, styles.boxTextPlayer, { textAlign: 'right' }]} >{game.robot.name}</Text>
                  }
                  {editPlayerTwo &&
                    <TextInput
                      style={styles.input}
                      onChangeText={setOnChangeTextp2}
                      onBlur={() => ChangeName('p2')}
                      value={onChangeTextp2}
                      style={styles.input}
                    />
                  }
                </View>
              }


            </View>
          }
          {GetGameIsOver(game) === true &&
            <View style={styles.playerBoxes} >
              <View style={[styles.playerBoxWin]} >
                <Animated.Text style={[styles.msgWin]} onPress={() => ResetGame()} >{whoWon()}  won</Animated.Text>
              </View>
            </View>
          }
          {GetGameIsOver(game) === false && game.plays.length === 9 &&
            <View style={styles.playerBoxes} >
              <View style={[styles.playerBoxWin]}>
                <Text style={[styles.msgDraw]} onPress={() => ResetGame()}>Draw!</Text>
              </View>
            </View>
          }

          {game.plays.length > 0 && game.plays.pop().player === 1 && GetGameIsOver(game) === false && game.plays.length === 9 &&
            playRobot()
          }

          <View style={[styles.containerCenter]} >

            <TouchableOpacity onPress={() => selectField('a1')}>
              <View style={[game.contrastA1 ? styles.selectedBox : styles.notSelectedBox, styles.box, GetGameIsOver(game) === false && game.plays.length === 9 ? styles.borderColorDraw : GetGameIsOver(game) ? styles.borderColorWin : styles.borderColor, styles.borderBottom, styles.borderRight, { borderTopLeftRadius: 5 }]}>
                <Text style={[styles.boxText, styles.selectedText]} >{game.a1}</Text>
              </View>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => selectField('a2')} >
              <View style={[game.contrastA2 ? styles.selectedBox : styles.notSelectedBox, styles.box, GetGameIsOver(game) === false && game.plays.length === 9 ? styles.borderColorDraw : GetGameIsOver(game) ? styles.borderColorWin : styles.borderColor, styles.borderBottom, styles.borderLeft, styles.borderRight]}>
                <Text style={[styles.boxText, styles.selectedText]} >{game.a2}</Text>
              </View>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => selectField('a3')} >
              <View style={[game.contrastA3 ? styles.selectedBox : styles.notSelectedBox, styles.box, GetGameIsOver(game) === false && game.plays.length === 9 ? styles.borderColorDraw : GetGameIsOver(game) ? styles.borderColorWin : styles.borderColor, styles.borderBottom, styles.borderLeft, { borderTopRightRadius: 5 }]}>
                <Text style={[styles.boxText, styles.selectedText]} >{game.a3}</Text>
              </View>
            </TouchableOpacity>
          </View>

          <View style={styles.containerCenter}>
            <TouchableOpacity onPress={() => selectField('b1')} >
              <View style={[game.contrastB1 ? styles.selectedBox : styles.notSelectedBox, styles.box, GetGameIsOver(game) === false && game.plays.length === 9 ? styles.borderColorDraw : GetGameIsOver(game) ? styles.borderColorWin : styles.borderColor, styles.borderTop, styles.borderBottom, styles.borderRight]}>
                <Text style={[styles.boxText, styles.selectedText]} >{game.b1}</Text>
              </View>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => selectField('b2')} >
              <View style={[game.contrastB2 ? styles.selectedBox : styles.notSelectedBox, styles.box, GetGameIsOver(game) === false && game.plays.length === 9 ? styles.borderColorDraw : GetGameIsOver(game) ? styles.borderColorWin : styles.borderColor, styles.borderTop, styles.borderBottom, styles.borderLeft, styles.borderRight]}>
                <Text style={[styles.boxText, styles.selectedText]} >{game.b2}</Text>
              </View>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => selectField('b3')} >
              <View style={[game.contrastB3 ? styles.selectedBox : styles.notSelectedBox, styles.box, GetGameIsOver(game) === false && game.plays.length === 9 ? styles.borderColorDraw : GetGameIsOver(game) ? styles.borderColorWin : styles.borderColor, styles.borderTop, styles.borderLeft, styles.borderBottom]}>
                <Text style={[styles.boxText, styles.selectedText]} >{game.b3}</Text>
              </View>
            </TouchableOpacity>
          </View>
          <View style={styles.containerCenter}>
            <TouchableOpacity onPress={() => selectField('c1')} >
              <View style={[game.contrastC1 ? styles.selectedBox : styles.notSelectedBox, styles.box, GetGameIsOver(game) === false && game.plays.length === 9 ? styles.borderColorDraw : GetGameIsOver(game) ? styles.borderColorWin : styles.borderColor, styles.borderTop, styles.borderRight, { borderBottomLeftRadius: 5 }]}>
                <Text style={[styles.boxText, styles.selectedText]} >{game.c1}</Text>
              </View>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => selectField('c2')} >
              <View style={[game.contrastC2 ? styles.selectedBox : styles.notSelectedBox, styles.box, GetGameIsOver(game) === false && game.plays.length === 9 ? styles.borderColorDraw : GetGameIsOver(game) ? styles.borderColorWin : styles.borderColor, styles.borderTop, styles.borderRight, styles.borderLeft]}>
                <Text style={[styles.boxText, styles.selectedText]} >{game.c2}</Text>
              </View>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => selectField('c3')} >
              <View style={[game.contrastC3 ? styles.selectedBox : styles.notSelectedBox, styles.box, GetGameIsOver(game) === false && game.plays.length === 9 ? styles.borderColorDraw : GetGameIsOver(game) ? styles.borderColorWin : styles.borderColor, styles.borderTop, styles.borderLeft, { borderBottomRightRadius: 5 }]}>
                <Text style={[styles.boxText, styles.selectedText]} >{game.c3}</Text>
              </View>
            </TouchableOpacity>
          </View>

          {/* </ImageBackground> */}
          <View style={styles.containerOptions}>
            <TouchableOpacity style={[styles.btnOptions]} onPress={() => ResetGame()} >
              <Text style={{ color: "#dc3545", fontSize: 23 }}>
                <MaterialCommunityIcons name="sticker-remove-outline" size={28} color="#dc3545" />
              </Text>
            </TouchableOpacity>
            <TouchableOpacity style={[styles.btnOptions]} onPress={() => setDarkMode(!darkMode)} >
              <Text style={[{ fontSize: 23 }, styles.textSecondary]}>
                {darkMode === true &&
                  <Entypo name="light-up" size={28} color="#e0e0e0" />
                }
                {darkMode === false &&
                  <Fontisto name="night-clear" size={28} color="#232323" />
                }

              </Text>
            </TouchableOpacity>
            {playingWithRobot === false &&
              <TouchableOpacity style={[styles.btnOptions]} onPress={() => setPlayingWithRobot(true)} >
                <Text style={{ color: "#dc3545", fontSize: 23 }}>
                  <FontAwesome5 name="robot" size={28} color="#6c757d" />
                </Text>
              </TouchableOpacity>
            }
            {playingWithRobot === true &&
              <TouchableOpacity style={[styles.btnOptions]} onPress={() => setPlayingWithRobot(false)} >
                <Text style={{ color: "#dc3545", fontSize: 23 }}>
                  <Feather name="users" size={28} color="#6c757d" />
                </Text>
              </TouchableOpacity>
            }


          </View>
        </ImageBackground>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    width: '100%'
  },
  containerCenter: {
    flexDirection: 'row',
  },
  containerOptions: {
    flexDirection: 'row',
