import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';

export default function App() {
  const [game, setGame] = useState({
    a1: '', a2: '', a3: '',
    b1: '', b2: '', b3: '',
    c1: '', c2: '', c3: '',
    playerOne: {
      name: "player 1",
      myTime: true,
      ballOrX: "x"
    },
    playertwo: {
      name: "player 2",
      myTime: false,
      ballOrX: "o"
    },
    plays: []
  });

  function next() {
    if (game.playerOne.myTime === true) {
      return game.playerOne.name;
    } else {
      game.playertwo.name;
    }
  }

  function msg() {
    if (game.playerOne.count == 0) {
      return " this is your time!";
    }
  }

  function cellIsFilled(cell) {

    if (cell == 'a1') {
      return game.a1 != '';
    } else if (cell == 'a2') {
      return game.a2 != '';
    } else if (cell == 'a3') {
      return game.a3 != '';
    } else if (cell == 'b1') {
      return game.b1 != '';
    } else if (cell == 'b2') {
      return game.b2 != '';
    } else if (cell == 'b3') {
      return game.b3 != '';
    } else if (cell == 'c1') {
      return game.c1 != '';
    } else if (cell == 'c2') {
      return game.c2 != '';
    } else if (cell == 'c3') {
      return game.c3 != '';
    }
  }

  function currentPlayerBallOrX() {
    if (game.playerOne.myTime) {
      return game.playerOne.ballOrX;
    } else {
      return game.playertwo.ballOrX;
    }
  }
  function currentPlayer() {
    return game.playerOne.myTime ? 1 : 2;
  }


  function SetCell(cell, ballOrX) {

    var auxnowGame = game;
    auxnowGame = JSON.stringify(auxnowGame);
    var nowGame = JSON.parse(auxnowGame);
    if (cell == 'a1') {
      nowGame.a1 = ballOrX;
    } else if (cell == 'a2') {
      nowGame.a2 = ballOrX;
    } else if (cell == 'a3') {
      nowGame.a3 = ballOrX;
    } else if (cell == 'b1') {
      nowGame.b1 = ballOrX;
    } else if (cell == 'b2') {
      nowGame.b2 = ballOrX;
    } else if (cell == 'b3') {
      nowGame.b3 = ballOrX;
    } else if (cell == 'c1') {
      nowGame.c1 = ballOrX;
    } else if (cell == 'c2') {
      nowGame.c2 = ballOrX;
    } else if (cell == 'c3') {
      nowGame.c3 = ballOrX;
    }
    setGame(nowGame);

  }


  function selectField(cell) {
    const isFilled = cellIsFilled(cell);

    if (isFilled == false) {
      const ballOrX = currentPlayerBallOrX();
      SetCell(cell, ballOrX);
      RecordPlay();
    }
  }

  return (
    <View style={styles.container}>
      <View style={styles.containerCenter}>
        <Text style={[styles.boxText]} >{next()} {msg()}</Text>
      </View>

      <View style={styles.containerCenter}>
        <TouchableOpacity onPress={() => selectField('a1')}>
          <View style={[styles.box, styles.borderColor, styles.borderBottom, styles.borderRight]}>
            <Text style={[styles.boxText]} >{game.a1}</Text>
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => selectField('a2')} >
          <View style={[styles.box, styles.borderColor, styles.borderBottom, styles.borderLeft, styles.borderRight]}>
            <Text style={[styles.boxText]} >{game.a2}</Text>
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => selectField('a3')} >
          <View style={[styles.box, styles.borderColor, styles.borderBottom, styles.borderLeft]}>
            <Text style={[styles.boxText]} >{game.a3}</Text>
          </View>
        </TouchableOpacity>
      </View>
      <View style={styles.containerCenter}>
        <TouchableOpacity onPress={() => selectField('b1')} >
          <View style={[styles.box, styles.borderColor, styles.borderTop, styles.borderBottom, styles.borderRight]}>
            <Text style={[styles.boxText]} >{game.b1}</Text>
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => selectField('b2')} >
          <View style={[styles.box, styles.borderColor, styles.borderTop, styles.borderBottom, styles.borderLeft, styles.borderRight]}>
            <Text style={[styles.boxText]} >{game.b2}</Text>
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => selectField('b3')} >
          <View style={[styles.box, styles.borderColor, styles.borderTop, styles.borderLeft, styles.borderBottom]}>
            <Text style={[styles.boxText]} >{game.b3}</Text>
          </View>
        </TouchableOpacity>
      </View>
      <View style={styles.containerCenter}>
        <TouchableOpacity onPress={() => selectField('c1')} >
          <View style={[styles.box, styles.borderColor, styles.borderTop, styles.borderRight]}>
            <Text style={[styles.boxText]} >{game.c1}</Text>
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => selectField('c2')} >
          <View style={[styles.box, styles.borderColor, styles.borderTop, styles.borderRight, styles.borderLeft]}>
            <Text style={[styles.boxText]} >{game.c2}</Text>
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => selectField('c3')} >
          <View style={[styles.box, styles.borderColor, styles.borderTop, styles.borderLeft]}>
            <Text style={[styles.boxText]} >{game.c3}</Text>
          </View>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  containerCenter: {
    flexDirection: 'row',

  },
  box: {
    height: 70,
    width: 70,
  },
  bgPrimary: {
    backgroundColor: '#007bff'
  },
  borderTop: {
    borderTopWidth: 1,
  },
  borderBottom: {
    borderBottomWidth: 1,
  },
  borderLeft: {
    borderLeftWidth: 1,
  },
  borderRight: {
    borderRightWidth: 1,
  },
  borderColor: {
    borderColor: '#007bff',
  },
  boxText: {
    fontSize: '25px',
    textAlign: 'center',
    alignSelf: 'center'
  }
});
