import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, TouchableOpacity, Animated, ImageBackground } from 'react-native';

export default function App() {
  const image = { uri: "https://i.pinimg.com/736x/b5/c4/57/b5c457223b8946d56cfefaa2b980d90f.jpg" };

  const [rotateAnimation, setRotateAnimation] = useState(new Animated.Value(0));

  const winningAnimation = () => {
    Animated.timing(rotateAnimation, {
      toValue: 1,
      duration: 800,
    }).start(() => {
      rotateAnimation.setValue(0);
    });
  };


  const interpolateRotating = rotateAnimation.interpolate({
    inputRange: [0, 1],
    outputRange: ['0deg', '720deg'],
  });

  const animatedStyle = {
    transform: [
      {
        rotate: interpolateRotating,
      },
    ],
  };

  const [darkMode, setDarkMode] = useState(true);

  const [game, setGame] = useState({
    a1: '', a2: '', a3: '',
    b1: '', b2: '', b3: '',
    c1: '', c2: '', c3: '',

    contrastA1: false, contrastA2: false, contrastA3: false,
    contrastB1: false, contrastB2: false, contrastB3: false,
    contrastC1: false, contrastC2: false, contrastC3: false,

    playerOne: {
      name: "player 1",
      myTime: true,
      ballOrX: "x"
    },
    playertwo: {
      name: "player 2",
      myTime: false,
      ballOrX: "o"
    },
    plays: []
  });

  function next() {
    if (game.playerOne.myTime === true) {
      return game.playerOne.name;
    } else {
      return game.playertwo.name;
    }
  }

  function msg() {
    if (game.playerOne.count == 0) {
      return " this is your time!";
    }
  }

  function cellIsFilled(cell) {

    if (cell == 'a1') {
      return game.a1 != '';
    } else if (cell == 'a2') {
      return game.a2 != '';
    } else if (cell == 'a3') {
      return game.a3 != '';
    } else if (cell == 'b1') {
      return game.b1 != '';
    } else if (cell == 'b2') {
      return game.b2 != '';
    } else if (cell == 'b3') {
      return game.b3 != '';
    } else if (cell == 'c1') {
      return game.c1 != '';
    } else if (cell == 'c2') {
      return game.c2 != '';
    } else if (cell == 'c3') {
      return game.c3 != '';
    }
  }

  function currentPlayerBallOrX() {
    return game.playerOne.myTime ? game.playerOne.ballOrX : game.playertwo.ballOrX;
  }

  function currentPlayer() {
    return game.playerOne.myTime ? 1 : 2;
  }

  function SetCell(updateGame, cell, ballOrX) {
    if (cell == 'a1') {
      updateGame.a1 = ballOrX;
    } else if (cell == 'a2') {
      updateGame.a2 = ballOrX;
    } else if (cell == 'a3') {
      updateGame.a3 = ballOrX;
    } else if (cell == 'b1') {
      updateGame.b1 = ballOrX;
    } else if (cell == 'b2') {
      updateGame.b2 = ballOrX;
    } else if (cell == 'b3') {
      updateGame.b3 = ballOrX;
    } else if (cell == 'c1') {
      updateGame.c1 = ballOrX;
    } else if (cell == 'c2') {
      updateGame.c2 = ballOrX;
    } else if (cell == 'c3') {
      updateGame.c3 = ballOrX;
    }
    return updateGame;
  }

  function RecordPlay(updateGame, current, ballOrX, cell) {
    const objPlay = { player: current, ballOrX: ballOrX, cell, dateTime: new Date() };
    updateGame.plays.push(objPlay);
    return updateGame;
  }

  function GetACopyOfTheObject() {
    var auxnowGame = game;
    auxnowGame = JSON.stringify(auxnowGame);
    return JSON.parse(auxnowGame);
  }

  function GetGameIsOver(updateGame) {
    if (updateGame.plays.length >= 5) {
      if (updateGame.a1 != '' && updateGame.a1 == updateGame.a2 && updateGame.a2 == updateGame.a3) {
        updateGame.contrastA1 = true;
        updateGame.contrastA2 = true;
        updateGame.contrastA3 = true;
        return true;
      }
      else if (updateGame.b1 != '' && updateGame.b1 == updateGame.b2 && updateGame.b2 == updateGame.b3) {
        updateGame.contrastB1 = true;
        updateGame.contrastB2 = true;
        updateGame.contrastB3 = true;
        return true;
      } else if (updateGame.a1 != '' && updateGame.a1 == updateGame.b1 && updateGame.b1 == updateGame.c1) {
        updateGame.contrastA1 = true;
        updateGame.contrastB1 = true;
        updateGame.contrastC1 = true;
        return true;
      } else if (updateGame.a1 != '' && updateGame.a1 == updateGame.b2 && updateGame.b2 == updateGame.c3) {
        updateGame.contrastA1 = true;
        updateGame.contrastB2 = true;
        updateGame.contrastC3 = true;
        return true;
      } else if (updateGame.a2 != '' && updateGame.a2 == updateGame.b2 && updateGame.b2 == updateGame.c2) {
        updateGame.contrastA2 = true;
        updateGame.contrastB2 = true;
        updateGame.contrastC2 = true;
        return true;
      } else if (updateGame.a3 != '' && updateGame.a3 == updateGame.b3 && updateGame.b3 == updateGame.c3) {
        updateGame.contrastA3 = true;
        updateGame.contrastB3 = true;
        updateGame.contrastC3 = true;
        return true;
      } else if (updateGame.c1 != '' && updateGame.c1 == updateGame.c2 && updateGame.c2 == updateGame.c3) {
        updateGame.contrastC1 = true;
        updateGame.contrastC2 = true;
        updateGame.contrastC3 = true;
        return true;
      } else if (updateGame.c1 != '' && updateGame.c1 == updateGame.b2 && updateGame.b2 == updateGame.a3) {
        updateGame.contrastC1 = true;
        updateGame.contrastB2 = true;
        updateGame.contrastA3 = true;
        return true;
      }
    }
    return false;
  }

  function ResetGame() {
    const bool = Math.random() < 0.5;
    setGame({
      a1: '', a2: '', a3: '',
      b1: '', b2: '', b3: '',
      c1: '', c2: '', c3: '',

      contrastA1: false, contrastA2: false, contrastA3: false,
      contrastB1: false, contrastB2: false, contrastB3: false,
      contrastC1: false, contrastC2: false, contrastC3: false,

      playerOne: {
        name: "player 1",
        myTime: bool,
        ballOrX: "x"
      },
      playertwo: {
        name: "player 2",
        myTime: !bool,
        ballOrX: "o"
      },
      plays: []
    })
  }

  function ChangePlayer(updateGame) {
    updateGame.playerOne.myTime = !updateGame.playerOne.myTime;
    updateGame.playertwo.myTime = !updateGame.playertwo.myTime;
    return updateGame;
  }

  function selectField(cell) {
    var updateGame = GetACopyOfTheObject();

    const isFilled = cellIsFilled(cell);
    const isOver = GetGameIsOver(updateGame);
    if (isOver == false) {
      if (isFilled == false) {

        const ballOrX = currentPlayerBallOrX();
        updateGame = SetCell(updateGame, cell, ballOrX);
        const current = currentPlayer();
        updateGame = RecordPlay(updateGame, current, ballOrX, cell);
        updateGame = ChangePlayer(updateGame);
        GetGameIsOver(updateGame);
        setGame(updateGame);
      }
    }
  }

  function whoWon() {
    const last = game.plays[game.plays.length - 1];
    if (last.player == 1) {
      return game.playerOne.name;
    } else {
      return game.playertwo.name;
    }

  }

  return (
    // <View style={[styles.container, { backgroundColor: darkMode ? "#000" : "#fff" }]}>
    <View style={styles.container}>
      {GetGameIsOver(game) === false && game.plays.length <= 8 &&
        <View style={styles.playerBoxes}>
          <View style={[styles.playerBox]}>
            <Text style={[game.playerOne.myTime ? styles.selectedPlayerBox : styles.notSelectedPlayerBox, styles.boxText]} >{game.playerOne.name}</Text>
          </View>
          <View style={[styles.playerBox]}>
            <Text style={[game.playertwo.myTime ? styles.selectedPlayerBox : styles.notSelectedPlayerBox, styles.boxText]} >{game.playertwo.name}</Text>
          </View>
        </View>
      }
      {GetGameIsOver(game) === true &&
        <View style={styles.playerBoxes} >
          <View style={[styles.playerBoxWin]} onPress={winningAnimation()}>
            <Animated.Text style={[styles.msgWin]} onPress={() => ResetGame()} >{whoWon()}  won</Animated.Text>
          </View>
        </View>
      }
      {GetGameIsOver(game) === false && game.plays.length === 9 &&
        <View style={styles.playerBoxes} >
          <View style={[styles.playerBoxWin]}>
            <Text style={[styles.msgDraw]} onPress={() => ResetGame()}>Draw!</Text>
          </View>
        </View>
      }

      <ImageBackground source={image} resizeMode="cover" style={[styles.image, {padding: 70}]}>
        <View style={styles.containerCenter}>

          <TouchableOpacity onPress={() => selectField('a1')}>
            <View style={[game.contrastA1 ? styles.selectedBox : styles.notSelectedBox, styles.box, styles.borderColor, styles.borderBottom, styles.borderRight, { borderTopLeftRadius: 5 }]}>
              <Text style={[styles.boxText, styles.selectedText]} >{game.a1}</Text>
            </View>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => selectField('a2')} >
            <View style={[game.contrastA2 ? styles.selectedBox : styles.notSelectedBox, styles.box, styles.borderColor, styles.borderBottom, styles.borderLeft, styles.borderRight]}>
              <Text style={[styles.boxText, styles.selectedText]} >{game.a2}</Text>
            </View>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => selectField('a3')} >
            <View style={[game.contrastA3 ? styles.selectedBox : styles.notSelectedBox, styles.box, styles.borderColor, styles.borderBottom, styles.borderLeft, { borderTopRightRadius: 5 }]}>
              <Text style={[styles.boxText, styles.selectedText]} >{game.a3}</Text>
            </View>
          </TouchableOpacity>
        </View>

        <View style={styles.containerCenter}>
          <TouchableOpacity onPress={() => selectField('b1')} >
            <View style={[game.contrastB1 ? styles.selectedBox : styles.notSelectedBox, styles.box, styles.borderColor, styles.borderTop, styles.borderBottom, styles.borderRight]}>
              <Text style={[styles.boxText, styles.selectedText]} >{game.b1}</Text>
            </View>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => selectField('b2')} >
            <View style={[game.contrastB2 ? styles.selectedBox : styles.notSelectedBox, styles.box, styles.borderColor, styles.borderTop, styles.borderBottom, styles.borderLeft, styles.borderRight]}>
              <Text style={[styles.boxText, styles.selectedText]} >{game.b2}</Text>
            </View>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => selectField('b3')} >
            <View style={[game.contrastB3 ? styles.selectedBox : styles.notSelectedBox, styles.box, styles.borderColor, styles.borderTop, styles.borderLeft, styles.borderBottom]}>
              <Text style={[styles.boxText, styles.selectedText]} >{game.b3}</Text>
            </View>
          </TouchableOpacity>
        </View>
        <View style={styles.containerCenter}>
          <TouchableOpacity onPress={() => selectField('c1')} >
            <View style={[game.contrastC1 ? styles.selectedBox : styles.notSelectedBox, styles.box, styles.borderColor, styles.borderTop, styles.borderRight, { borderBottomLeftRadius: 5 }]}>
              <Text style={[styles.boxText, styles.selectedText]} >{game.c1}</Text>
            </View>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => selectField('c2')} >
            <View style={[game.contrastC2 ? styles.selectedBox : styles.notSelectedBox, styles.box, styles.borderColor, styles.borderTop, styles.borderRight, styles.borderLeft]}>
              <Text style={[styles.boxText, styles.selectedText]} >{game.c2}</Text>
            </View>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => selectField('c3')} >
            <View style={[game.contrastC3 ? styles.selectedBox : styles.notSelectedBox, styles.box, styles.borderColor, styles.borderTop, styles.borderLeft, { borderBottomRightRadius: 5 }]}>
              <Text style={[styles.boxText, styles.selectedText]} >{game.c3}</Text>
            </View>
          </TouchableOpacity>
        </View>

      </ImageBackground>
      <View style={styles.containerCenter}>
        <TouchableOpacity style={[styles.btnOptions]} onPress={() => ResetGame()} >
          <Text style={{ color: "#dc3545", fontSize: 23 }}>Reset</Text>
        </TouchableOpacity>
        <TouchableOpacity style={[styles.btnOptions]} onPress={() => setDarkMode(!darkMode)} >
          <Text style={[{ fontSize: 23 }, styles.textSecondary]}>
            {darkMode === true &&
              "Dark mode"
            }
            {darkMode === false &&
              "Light mode"
            }

          </Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

